export const BubbleSortAnimations = (array) => {
    const animations = [];

    for (let i = 0; i < arrray.length - 1; i++) {
        for (let j = 0; j < array.length - i - 1; j++) {
            animations.push[j, j+1];
            animations.push[j, j+1];
            
            if(arr[j] > arr[j+1]) {
                const temp = arr[j];
                arr[j] = arr[j+1];
                arr[j+1] = temp;
            }
        }
    }
}
