export const BubbleSortAnimations = (array) => {
    const animations = [];

    let k = 0;

    for (let i = 0; i < array.length - 1; i++) {
        for (let j = 0; j < array.length - i - 1; j++) {
            animations.push([j, j+1]);
            animations.push([j, j+1]);

            if(array[j] > array[j+1]) {
                animations.push([k++, array[j+1]]);

                const temp = array[j];
                array[j] = array[j+1];
                array[j+1] = temp;
            }
        }
    }

    console.log(array);
    return animations;
}
